import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FaThermometerHalf, FaTint, FaWind, FaEye } from 'react-icons/fa';

const WeatherContainer = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 15px;
  margin-bottom: 20px;
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const WeatherTitle = styled.h3`
  color: white;
  font-size: 16px;
  margin: 0 0 15px 0;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const WeatherGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 12px;
`;

const WeatherItem = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  color: rgba(255, 255, 255, 0.9);
  font-size: 14px;
`;

const WeatherIcon = styled.div`
  color: rgba(255, 255, 255, 0.7);
  font-size: 16px;
`;

const WeatherValue = styled.span`
  font-weight: 600;
`;

const LoadingText = styled.div`
  color: rgba(255, 255, 255, 0.6);
  font-size: 14px;
  text-align: center;
  padding: 20px 0;
`;

const ErrorText = styled.div`
  color: rgba(255, 100, 100, 0.8);
  font-size: 14px;
  text-align: center;
  padding: 10px 0;
`;

const WeatherWidget = ({ location }) => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (location && location.trim()) {
      fetchWeatherData(location);
    } else {
      setWeatherData(null);
      setError(null);
    }
  }, [location]);

  const fetchWeatherData = async (loc) => {
    setLoading(true);
    setError(null);

    try {
      // Call our backend API for weather simulation
      const response = await fetch(`/api/weather?location=${encodeURIComponent(loc)}`);

      if (response.ok) {
        const data = await response.json();
        setWeatherData(data);
      } else {
        // Fallback to simulated data
        await new Promise(resolve => setTimeout(resolve, 500));

        const mockWeatherData = {
          temperature: Math.round(Math.random() * 20 + 15), // 15-35¬∞C
          humidity: Math.round(Math.random() * 40 + 40), // 40-80%
          windSpeed: Math.round(Math.random() * 15 + 5), // 5-20 km/h
          visibility: Math.round(Math.random() * 5 + 5) // 5-10 km
        };

        setWeatherData(mockWeatherData);
      }
    } catch (err) {
      // Fallback to simulated data on error
      try {
        await new Promise(resolve => setTimeout(resolve, 500));

        const mockWeatherData = {
          temperature: Math.round(Math.random() * 20 + 15),
          humidity: Math.round(Math.random() * 40 + 40),
          windSpeed: Math.round(Math.random() * 15 + 5),
          visibility: Math.round(Math.random() * 5 + 5)
        };

        setWeatherData(mockWeatherData);
      } catch (fallbackErr) {
        setError('Unable to fetch weather data');
        console.error('Weather fetch error:', err);
      }
    } finally {
      setLoading(false);
    }
  };

  if (!location) {
    return (
      <WeatherContainer>
        <WeatherTitle>
          üå§Ô∏è Weather Info
        </WeatherTitle>
        <LoadingText>Enter location to see weather</LoadingText>
      </WeatherContainer>
    );
  }

  if (loading) {
    return (
      <WeatherContainer>
        <WeatherTitle>
          üå§Ô∏è Weather Info
        </WeatherTitle>
        <LoadingText>Loading weather data...</LoadingText>
      </WeatherContainer>
    );
  }

  if (error) {
    return (
      <WeatherContainer>
        <WeatherTitle>
          üå§Ô∏è Weather Info
        </WeatherTitle>
        <ErrorText>{error}</ErrorText>
      </WeatherContainer>
    );
  }

  if (!weatherData) {
    return null;
  }

  return (
    <WeatherContainer>
      <WeatherTitle>
        üå§Ô∏è {location}
      </WeatherTitle>

      <WeatherGrid>
        <WeatherItem>
          <WeatherIcon>
            <FaThermometerHalf />
          </WeatherIcon>
          <div>
            <WeatherValue>{weatherData.temperature}¬∞C</WeatherValue>
          </div>
        </WeatherItem>

        <WeatherItem>
          <WeatherIcon>
            <FaTint />
          </WeatherIcon>
          <div>
            <WeatherValue>{weatherData.humidity}%</WeatherValue>
          </div>
        </WeatherItem>

        <WeatherItem>
          <WeatherIcon>
            <FaWind />
          </WeatherIcon>
          <div>
            <WeatherValue>{weatherData.windSpeed} km/h</WeatherValue>
          </div>
        </WeatherItem>

        <WeatherItem>
          <WeatherIcon>
            <FaEye />
          </WeatherIcon>
          <div>
            <WeatherValue>{weatherData.visibility} km</WeatherValue>
          </div>
        </WeatherItem>
      </WeatherGrid>
    </WeatherContainer>
  );
};

export default WeatherWidget;
